package r01f.cache.mock;

public class MockCachedObjectFactory  {
/////////////////////////////////////////////////////////////////////////////////////////
//
/////////////////////////////////////////////////////////////////////////////////////////
	public static MockCachedObject create(final MockOID oid) {
		return _buildMockObject(oid);
	}
/////////////////////////////////////////////////////////////////////////////////////////
//  ENTITY PROVIDER
/////////////////////////////////////////////////////////////////////////////////////////
	private static MockCachedObject _buildMockObject(final MockOID oid) {
		MockCachedObject theMockObject = new MockCachedObject();
		theMockObject.setOid(oid);
		theMockObject.setSomeDescription("..some description");
		return theMockObject;
	}
/////////////////////////////////////////////////////////////////////////////////////////
//
/////////////////////////////////////////////////////////////////////////////////////////
}
